const burger=document.getElementById("burger"),menu=document.getElementById("menu"),scrollBtn=document.querySelector(".scroll-btn");function show(){menu.style.top=0}function close(){menu.style.top="-150%"}window.addEventListener("scroll",()=>{document.body.scrollTop>20||document.documentElement.scrollTop>20?scrollBtn.style.display="block":scrollBtn.style.display="none"}),scrollBtn.addEventListener("click",()=>{window.scroll({top:0,behavior:"smooth"})}),close(),burger.addEventListener("click",show),menu.addEventListener("click",close);